declare @input varchar(max) = '';

-- Create a nubers table
drop table if exists #n;
select top (100000) num = row_number() over (order by a.object_id) into #n from sys.columns a cross join sys.columns b;

-- Turn the input string into a table.
-- One row per letter. Alogn with each letter also generate the last (including current) 4 letters token 
drop table if exists dbo.ch05_input;
select
    num as id,
    substring(@input, num, 1) as last_letter,
    substring(@input, num-3, 1) + ',' + substring(@input, num-2, 1) 
        + ',' + substring(@input, num-1, 1)+ ',' + substring(@input, num, 1) as [token]
into
    dbo.ch05_input
from
    #n
where
    num <= len(@input);

drop table if exists #temp;
select
    i.*,
    t.[value] as token_letter
into
    #temp
from
    dbo.ch05_input i
cross apply
    string_split(token, ',') t;

with cte as
(
    select 
        id, last_letter, token,
        count(distinct [token_letter]) as dtl
    from
        #temp
    where
        [token_letter] != ''
    group by
        id, last_letter, token
)
select top(1) * from cte where dtl = 4 order by id


-- 1287