declare @input varchar(max) = '';

drop table if exists #n;
select top (100000) num = row_number() over (order by a.object_id) into #n from sys.columns a cross join sys.columns b;

drop table if exists dbo.ch05_input;
select
    num as id,
    substring(@input, num, 1) as last_letter,
    substring(@input, num-13, 14)  as [token]
into
    dbo.ch05_input
from
    #n
where
    num <= len(@input);

--select * from dbo.ch05_input order by id;

drop table if exists #step1;
select
    i.*,
    substring(token, n.num, 1) as token_letter
into
    #step1
from
    dbo.ch05_input i
cross join
    #n n where n.num <= len(i.token);

--select * from #step1 order by id;

with cte as
(
    select 
        id, last_letter, token,
        count(distinct [token_letter]) as dl
    from
        #step1
    where
        [token_letter] != ''
    group by
        id, last_letter, token
) 
--select * from cte order by id
select top(1) * from cte where dl = 14 order by id

